sys_variable_value.value:
(function(outputs, steps, params, stepResult, assertEqual) {
    describe('KLF_GlideRecordUtils.deleteRecordTemplate', function() {
		var task1Name = 'KLF_GlideRecordUtils task1';
		var task2Name = 'KLF_GlideRecordUtils task2';
		beforeAll(function() {
			// create a couple tasks as test data
			var task1 = new GlideRecord('task');
			task1.short_description = task1Name;
			task1.update();


			var task2 = new GlideRecord('task');
			task2.short_description = task2Name;
			task2.update();

		});

		afterAll(function() {
			// cleanup created tasks
			var task = new GlideRecord('task');
			task.addQuery('short_description', task1Name).addOrCondition('short_description', task2Name);
			task.deleteMultiple();
		});

		it('should delete only the records in the query', function(){
			// at this point there should be 2 tasks created by beforeAll
			// i'm going to target deleting one of them
			var taskDelete = new GlideRecord('task');
			taskDelete.addQuery('short_description', task1Name);
			new global.KLF_GlideRecordUtils().deleteRecordTemplate('task', taskDelete.getEncodedQuery());

			// one should be deleted now. query for both and should get one back
			var task = new GlideRecord('task');
			task.addQuery('short_description', task1Name).addOrCondition('short_description', task2Name);
			task.query();
			expect(task.getRowCount()).toBe(1);
		});
	});

})(outputs, steps, params, stepResult, assertEqual);

jasmine.getEnv().execute();
